async function googleSearch(query, env) {
  const url = `https://www.googleapis.com/customsearch/v1?key=${env.GOOGLE_API_KEY}&cx=${env.GOOGLE_CSE_ID}&q=${encodeURIComponent(
    query,
  )}&num=10`;

  const headers = new Headers(standardHeaders);
  headers.set("Referer", "https://www.google.com/");
  headers.delete("cf-connecting-ip");
  headers.delete("cf-ipcountry");
  headers.delete("cf-ray");
  headers.delete("cf-visitor");

  try {
    const response = await fetch(url, { headers });
    const data = await response.json();

    return (
      data.items?.map((item) => ({
        title: item.title,
        link: item.link,
        description: item.snippet,
      })) || []
    );
  } catch (error) {
    console.error("Google Search Error:", error);
    return [];
  }
}
async function translateWithGemini(text, env) {
  const API_KEY = env.GEMINI_API_KEY;
  const MODEL_NAME = "gemini-2.0-flash-exp";
  const url = `https://generativelanguage.googleapis.com/v1beta/models/${MODEL_NAME}:generateContent?key=${API_KEY}`;

  const prompt = `Please translate the following English text to Persian (Farsi). Keep astronomical terms accurate and maintain the scientific tone:

English text: "${text}"

Translation guidelines:
- Maintain scientific accuracy
- Keep astronomical terms precise
- Use formal Persian
- Preserve the original meaning
- Make it easy to understand for Persian readers
- please do not add any other words to translatin like: 'Okay, here's the Persian (Farsi) translation, adhering to your guidelines:' or something elses`;

  const requestBody = {
    contents: [{
      parts: [{
        text: prompt
      }]
    }]
  };

  try {
    const response = await fetch(url, {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify(requestBody)
    });

    const data = await response.json();
    return data.candidates?.[0]?.content?.parts?.[0]?.text || "ترجمه در دسترس نیست.";
  } catch (error) {
    console.error("Translation Error:", error);
    return "خطا در ترجمه متن.";
  }
}

async function getNasaAPOD(env) {
  const NAPI_KEY = env.NASA_API_KEY || 'DEMO_KEY';
  const url = `https://api.nasa.gov/planetary/apod?api_key=${NAPI_KEY}`;

  try {
    const response = await fetch(url);
    const data = await response.json();

    // Translate the explanation to Persian
    const translatedExplanation = await translateWithGemini(data.explanation, env);

    return {
      ...data,
      explanation_fa: translatedExplanation
    };
  } catch (error) {
    console.error("NASA APOD Error:", error);
    return null;
  }
}

async function ddgSearch(query) {
  const encodedQuery = encodeURIComponent(query);
  const url = `https://html.duckduckgo.com/html/?q=${encodedQuery}`;

  try {
    const response = await fetch(url, {
      headers: new Headers(standardHeaders),
      redirect: 'follow'
    });

    const html = await response.text();
    const results = [];
    const regex = /<a rel="nofollow" class="result__a" href="(.*?)">(.*?)<\/a>/g;
    let match;

    while ((match = regex.exec(html)) !== null && results.length < 10) {
      try {
        const encodedUrl = match[1];
        const redirectUrl = new URL(encodedUrl, 'https://html.duckduckgo.com');
        const actualUrl = redirectUrl.searchParams.get("uddg") || encodedUrl;

        results.push({
          title: match[2],
          link: actualUrl,
          description: ""
        });
      } catch (urlError) {
        console.warn("Invalid URL encountered:", urlError);
        continue;
      }
    }

    return results;
  } catch (error) {
    console.error("DDG Search Error:", error);
    return [];
  }
}
async function nytArticleSearch(query, env, filter = "") {
  const apiKey = env.NYT_API_KEY; // Get your API key from environment variables
  const baseUrl = "https://api.nytimes.com/svc/search/v2/articlesearch.json";

  // Construct the query parameters
  let queryParams = `?q=${encodeURIComponent(query)}&api-key=${apiKey}`;
  if (filter) {
    queryParams += `&fq=${encodeURIComponent(filter)}`;
  }

  const url = baseUrl + queryParams;

  const headers = new Headers(standardHeaders);
  headers.delete("cf-connecting-ip");
  headers.delete("cf-ipcountry");
  headers.delete("cf-ray");
  headers.delete("cf-visitor");

  try {
    const response = await fetch(url, { headers });
    const data = await response.json();

    if (data.status !== "OK") {
      console.error("NYT Article Search Error:", data);
      return [];
    }

    // Extract relevant information from the response
    const results = data.response.docs.map((doc) => ({
      title: doc.headline.main,
      link: doc.web_url,
      description: doc.abstract || doc.snippet || "", // Use abstract or snippet
      pubDate: doc.pub_date,
    }));

    return results;
  } catch (error) {
    console.error("NYT Article Search Error:", error);
    return [];
  }
}
