async function wikipediaSearch(query) {
  const url = `https://en.wikipedia.org/w/api.php?action=query&format=json&list=search&srsearch=${encodeURIComponent(
    query,
  )}&srlimit=10`;

  const headers = new Headers(standardHeaders);
  headers.set("Referer", "https://en.wikipedia.org/");
  headers.delete("cf-connecting-ip");
  headers.delete("cf-ipcountry");
  headers.delete("cf-ray");
  headers.delete("cf-visitor");

  try {
    const response = await fetch(url, { headers });
    const data = await response.json();

    return data.query.search.map((result) => ({
      title: result.title,
      link: `https://en.wikipedia.org/wiki/${encodeURIComponent(result.title)}`,
      description: result.snippet,
    }));
  } catch (error) {
    console.error("Wikipedia Search Error:", error);
    return [];
  }
}

