async function handleRequest(request, env, ctx) {
  const corsHeaders = {
    "Access-Control-Allow-Origin": "https://nse.aihoore.ir",
    "Access-Control-Allow-Methods": "GET,POST,OPTIONS",
    "Access-Control-Allow-Headers": "Content-Type",
  };




  const url = new URL(request.url);

  if (request.method === "OPTIONS") {
    return new Response(null, { headers: corsHeaders });
  }

  if (url.pathname === "/proxy") {
    const proxyUrl = url.searchParams.get("url");
    if (!proxyUrl) {
      return new Response("No URL provided for proxy", { status: 400 });
    }

    try {
      const modifiedHeaders = new Headers(standardHeaders);
      const originalReferer = request.headers.get("Referer");
      modifiedHeaders.set("Referer", originalReferer || "https://www.google.com/");
      modifiedHeaders.set("User-Agent", standardUserAgent);
      modifiedHeaders.delete("cf-connecting-ip");
      modifiedHeaders.delete("cf-ipcountry");
      modifiedHeaders.delete("cf-ray");
      modifiedHeaders.delete("cf-visitor");

      const response = await fetch(proxyUrl, {
        headers: modifiedHeaders,
        redirect: "follow",
      });

      const text = await response.text();
      const headers = new Headers(response.headers);
      headers.delete("content-security-policy");

      return new Response(text, { status: response.status, headers: headers });
    } catch (error) {
      console.error("Proxy error:", error);
      return new Response("Error fetching URL through proxy", {
        status: 500,
      });
    }
  }

  if (request.method === "POST" && url.pathname === "/model") {
    const body = await request.json();
    const answer = await queryGemini(body.query, body.searchResults, env);

    return new Response(JSON.stringify({ answer }), {
      headers: { "Content-Type": "application/json", ...corsHeaders },
    });
  }

  if (request.method === "POST") {
    const body = await request.json();
    const query = body.query;

    if (!query) {
      return new Response(JSON.stringify({ error: "No query provided" }), {
        status: 400,
        headers: { "Content-Type": "application/json", ...corsHeaders },
      });
    }

    try {
      const [
        googleResults,
        ddgResults,
        wikipediaResults,
        sepResults,
        qwantResults,
        ddgSearchNewsResults,
        nytResults,
      ] = await Promise.all([
        googleSearch(query, env),
        ddgSearch(query),
        wikipediaSearch(query),
        sepSearch(query),
        qwantSearch(query),
        ddgSearchNews(query),
        nytArticleSearch(query, env),
      ]);

      const response = {
        google: googleResults,
        ddg: ddgResults,
        wikipedia: wikipediaResults,
        sep: sepResults,
        qwant: qwantResults,
        ddgSN: ddgSearchNewsResults,
        nyt: nytResults,
      };

      return new Response(JSON.stringify(response), {
        headers: { "Content-Type": "application/json", ...corsHeaders },
      });
    } catch (error) {
      return new Response(JSON.stringify({ error: error.message }), {
        status: 500,
        headers: { "Content-Type": "application/json", ...corsHeaders },
      });
    }
  }

  if (request.method === "GET" && url.pathname === "/") {
    return new Response(htmlContent, {
      headers: { "Content-Type": "text/html", ...corsHeaders },
    });
  }
  if (request.method === "GET" && url.pathname === "/nasa-apod") {
    try {
      const apodData = await getNasaAPOD(env);
      return new Response(JSON.stringify(apodData), {
        headers: {
          "Content-Type": "application/json",
          ...corsHeaders
        }
      });
    } catch (error) {
      return new Response(JSON.stringify({ error: error.message }), {
        status: 500,
        headers: { "Content-Type": "application/json", ...corsHeaders },
      });
    }
  }

  if (request.method === "POST" && url.pathname === "/generate-image") {
    const body = await request.json();
    const { prompt } = body;

    if (!prompt) {
      return new Response(JSON.stringify({ error: "No prompt provided" }), {
        status: 400,
        headers: { "Content-Type": "application/json", ...corsHeaders },
      });
    }

    try {
      const inputs = {
        prompt,
        width: 512,
        height: 512,
        num_steps: 20,
        guidance: 7.5,
      };

      const response = await env.AI.run(
        "@cf/runwayml/stable-diffusion-v1-5",
        inputs
      );

      return new Response(response, {
        headers: {
          "Content-Type": "image/png",
          ...corsHeaders
        },
      });
    } catch (error) {
      console.error("Image generation error:", error);
      return new Response(JSON.stringify({ error: "Failed to generate image" }), {
        status: 500,
        headers: { "Content-Type": "application/json", ...corsHeaders },
      });
    }
  }
  return new Response("Not found", { status: 404, headers: corsHeaders });
}

export default {
  async fetch(request, env, ctx) {

    return handleRequest(request, env, ctx);
  },
};
